import { Component, OnInit } from '@angular/core';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';



@Component({
  selector: 'app-login',
  templateUrl:'./login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent {

  isLoggedIn = false;

  public loginForm = new FormGroup({
    username: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required]),
  });

  myfield = "";

  constructor(private auth: AuthService, private router: Router){}

  login(){
    this.auth.login(this.loginForm.value).subscribe(success =>{
      console.log("Login Successful ", success);
      this.isLoggedIn = true;
      this.router.navigate(['components/dashboards']);
    },
    error => {
      console.log("Login Failed", error);
    });
  }

  signUp(){
    this.router.navigate(['component/dashboard']);
  }
}

// src\app\components\dashboards

<div class="login-container">
    <form [formGroup]="loginForm" (ngSubmit)="login()">

    <h2>Login</h2>

        <div>
            <label>Email: *</label>
            <input id="name" type="text" formControlName="username">
        </div>
        <div>
            <label>Password: *</label>
            <input id="password" type="password" formControlName="password">
        </div>
        <div>
            <button [disabled]="!loginForm.valid">Login</button>
        </div>

        <div *ngIf="isLoggedIn">
            <p>Login successful!</p>
        </div>

        <div>
            <p>OR</p>
        </div>




    </form>

    <button (click)="signUp()">Sign Up!!</button>

</div>
