import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Department } from 'src/app/models/department'; 
import { DepartmentService } from 'src/app/services/department.service'; 


@Component({
  selector: 'app-department',
  templateUrl: './department.component.html',
  styleUrls: ['./department.component.css']
})
export class DepartmentComponent implements OnInit {
  departments: Department[] = [];

  students: any;
  showAddDepartment = false;
  deptCreated= false;
  showUpdateDepartment = false;

  department : Department = {hod:'',  staffCount: 0,  phone: '',  email: '',  description:'',  deptId:0,  deptName: ''};

  hod = '';
  staffCount = 0;
  phone = '';
  email = '';
  description = '';
  deptId = 0;
  deptName = '';

  constructor(private departmentService: DepartmentService, private router: Router, private formBuilder: FormBuilder) { 

  }

  deptForm!: FormGroup;
  deptFormU!: FormGroup;

  showDepartment(){
    if(this.showAddDepartment == false){
      this.showAddDepartment = true;
    }
    else
    this.showAddDepartment = false;

  }
  showUpdateForm(department: Department){
    this.department = department;
    this.deptFormU = this.formBuilder.group({
      'department.hod': [this.department.hod, Validators.required],
      'department.staffCount': [this.department.staffCount, Validators.required],
      'department.email': [this.department.email, Validators.required],
      'department.description': [this.department.description, Validators.required],
      'department.phone': [this.department.phone, Validators.required],
      'department.deptName': [this.department.deptName, Validators.required],
      'department.deptId': [this.department.deptId, Validators.required],

    });
    if(this.showUpdateDepartment == false){
      this.showUpdateDepartment = true;
    }
    else
    this.showUpdateDepartment = false;
  }




  navBack(){
    this.router.navigate(['dashboard']);
  }

  ngOnInit() {
    this.deptForm = this.formBuilder.group({
      hod: [null, Validators.required],
      staffCount: [null, Validators.required],
      email: [null, Validators.required],
      description: [null, Validators.required],
      phone: [null, Validators.required],
      deptName: [null, Validators.required],
      id: [null, Validators.required],

    });
    this.getDepartments();
  }



  getDepartments() {
    this.departmentService.getDepartments().subscribe(
      departments => {
        this.departments = departments;
      },
      error => {
        console.log(error);
      }
    );
  }

  getDepartmentById(department: Department){
    this.departmentService.getDepartmentById(department.deptId).subscribe(
      response => {
        this.getDepartments();
      },
      error => {
        console.log(error);
      }
    );
  }

  addDepartment(department: Department) {
    this.departmentService.addDepartment(department).subscribe(
      response => {
        this.getDepartments();
        console.log("Success");
      },
      error => {
        console.log(error);
      }
    );
  }

  updateDepartment(id: number,department: Department) {   
    this.departmentService.updateDepartment(id, department).subscribe(
      response => {
        this.getDepartments();
        this.deptForm.reset();
        alert("Department updated successfully!")
        this.showDepartment();
      },
      error => {
        console.log(error);
      }
    );
  }



  deleteDepartment(id: number) {
    alert("Are you sure you want to delete this department?");
    this.departmentService.deleteDepartment(id).subscribe(
      response => {
        this.getDepartments();
      },
      error => {
        console.log(error);
      }
    );
  }

  createDepartment(department: Department) {
    this.departmentService.createDepartment(department).subscribe(
      createdDepartment => {
        console.log('Department created successfully:', createdDepartment);
        this.getDepartments();
        this.deptForm.reset();
        alert("Department created successfully!")
        this.showDepartment();
      },
      error => {
        console.log('Error occurred while creating department:', error);
      }
    );
  }
}
