import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Subjects } from '../models/subjects';

@Injectable({
  providedIn: 'root'
})
export class SubjectsService {
  private apiUrl = '/api/subject'; // Replace with your backend API URL

  constructor(private http: HttpClient) { }

  getSubjects(): Observable<Subjects[]> {
    return this.http.get<Subjects[]>('http://localhost:8080/api/student/getAll');
  }

  addSubject(subject: Subjects): Observable<Subjects> {
    return this.http.post<Subjects>('http://localhost:8080/api/student/add', subject);
  }

  updateSubject(id: number, subject: Subjects): Observable<Subjects> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.put<Subjects>(url, subject);
  }

  deleteSubject(id: number): Observable<void> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.delete<void>(url);
  }

  getAllSubjects(): Observable<Subjects[]> {
    return this.http.get<Subjects[]>(this.apiUrl);
  }

  createSubject(subject: Subjects): Observable<Subjects> {
    return this.http.post<Subjects>(this.apiUrl, subject);
  }
}
