import { Component, Input } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { BsModalService, BsModalRef } from 'ngx-bootstrap/modal';
import { Student } from 'src/app/models/student';
import { StudentService } from 'src/app/services/student.service';
import { StudentComponent } from '../../student/student.component';

@Component({
  selector: 'app-update-student',
  templateUrl: './update-student.component.html',
  styleUrls: ['./update-student.component.css']
})
export class UpdateStudentComponent {

  @Input() data: any;

  students: Student[] = [];
  student!: Student;
  s!: StudentComponent;

  studentId: number =0;

  id!:number;

  usnno = '';
  firstName = '';
  middleName = '';
  lastName = '';
  fatherName = '';
  motherName = '';
  parentEmail = '';
  fatherPhone = '';
  motherPhone = '';
  gender = '';
  dob = '';
  email = '';
  phone = '';
  yearOfAdmission = '';
  address = '';
  // studentId!: number;
  teacherGuardian = '';

  constructor(private studentService: StudentService, private router: Router, 
    private formBuilder: FormBuilder, private modalService: BsModalService, public bsModalRef: BsModalRef) { }



  ngOnInit() {

    this.studentForm = this.formBuilder.group({
      firstName: [this.student.firstName, Validators.required],
      middleName: [this.student.middleName],
      lastName: [this.student.lastName, Validators.required],
      usnno: [this.student.usnno, Validators.required],
      email: [this.student.email, [Validators.required, Validators.email]],
      phone: [this.student.phone, Validators.required],
      dob: [this.student.DOB, Validators.required],
      gender: [this.student.gender, Validators.required],
      yearOfAdmission: [this.student.yearOfAdmission, [Validators.required]],
      address: [this.student.address, [Validators.required]],
      fatherName: [this.student.fatherName, [Validators.required]],
      fatherPhone: [this.student.fatherPhone, [Validators.required]],
      motherName: [this.student.motherName, [Validators.required]],
      motherPhone: [this.student.motherPhone, [Validators.required]],
      parentEmail: [this.student.parentEmail, [Validators.required, Validators.email]],
      teacherGuardian: [this.student.teacherGuardian, [Validators.required]]
    });

  }

  getStudentById(studentId: number) {
    this.studentService.getStudentById(studentId)
    .subscribe(student => this.student = student);
  }

  close() {
    this.bsModalRef.hide();
  }

  studentForm!: FormGroup;


  showUpdateForm(student: Student){
    this.student = student;
    console.log(student.studentId);



  }

  updateStudent(student: Student){

  }




}
