import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Student } from 'src/app/models/student';
import { MarksService } from 'src/app/services/marks.service';

@Component({
  selector: 'app-marks',
  templateUrl: './marks.component.html',
  styleUrls: ['./marks.component.css']
})
export class MarksComponent {
  students: Student[] = [];
  subject: string = '';
  lectureNo: number = 0;
  showAttendance = false;

  constructor(private http: HttpClient, private router: Router, private marksService: MarksService) { }

  ngOnInit() {
    this.fetchStudents();

  }

  showAttendanceStatus(){
    if(this.showAttendance==false)
    this.showAttendance=true;
    else
    this.showAttendance=false;
  }

  fetchStudents() {
    this.http.get<any[]>('http://localhost:8080/api/student/getAll').subscribe(
      data => this.students = data,
      error => console.log(error)
    );

  }

  markAttendance(studentId: number, present: boolean, subject: string, lectureNo: number, i: number) {
    if(subject=='' && lectureNo == 0){
      alert("Please enter subject and lecture no.")
    }
    else if (subject==''){
      alert("Please enter subject")
    }
    else if (lectureNo==0){
      alert("Please enter lecture no.")
    }
    else{
      const data = {
        lectureNo,
        subject,
        studentId,
        present 
      };


      this.http.post('http://localhost:8080/api/marks/add', data).subscribe(
        () => console.log('Attendance marked successfully.'),
        error => console.log(error)
      );

      }


    }

    navBack(){
      this.router.navigate(['dashboard']);
    }





}
