import { Marks } from 'src/app/models/marks';
import { ViewAllMarksComponent } from '../marks-operations/view-all-marks/view-all-marks.component';
import { Subject } from 'rxjs';
import { SubjectsService } from 'src/app/services/subjects.service';
import { NgPipesModule, NgArrayPipesModule } from 'ngx-pipes';

@Component({
  selector: 'app-marks',
  templateUrl: './marks.component.html',
  styleUrls: ['./marks.component.css']
})
export class MarksComponent {
  students: Student[] = [];
  subject: string = '';
  lectureNo: number = 0;
  showMessageIndex: number | null = null;
  showUpdateMarksForm = false;

  noOfSubjects = 0;

  showUpdateForm: boolean[] = [false];

  subjects: Subjects[] = [];



  marksForm! : FormGroup;
  marks1 : Marks[] = [];
  subjects1: Subjects[] = [];
  student1: Student[] = [];
  marks2 : Marks[] = [];



  // @ViewChild('markForInput', { static: false }) markForInput: ElementRef;
  markForInput: number = 0;

  constructor(private http: HttpClient, private subjectService: SubjectsService, private studentService: StudentService, private router: Router, private marksService: MarksService, private formBuilder: FormBuilder, private modalService: BsModalService) {
    this.markForInput = 0;
   }

  ngOnInit() {
    this.marksForm = this.formBuilder.group({
      marks: [null, Validators.required]
    });

    this.fetchStudents();
    this.fetchSubjects();

    // this.noOfSubjects= this.sizeOf(this.subjects);

    // console.log(this.noOfSubjects);

    this.getMarks();
    this.getSubjects();
    // this.getStudents();
    // console.log(this.student1)
    // this.getMarksByStudentIdOneByOne(this.student1);
    // console.log(this.marks2)

  }

  loadStudents() {
    this.studentService.getStudents().subscribe(
      students => {
        this.students = students;
      },
      error => {
        console.log('Error occurred while fetching students:', error);
      }
    );
  }

  count = 0;

  // const subject = subjects[i];
  // const marks = marksData[i];

  // const subjectMarks = {
  //   subject: subject,
  //   marks: marks
  // };

  // studentMarks.push(subjectMarks);

  studentMarks = [];

  // getStudents() {
  //   this.studentService.getStudents().subscribe(
  //     student1 => {
  //       this.student1 = student1;
  //       this.marksService.getMarks().subscribe(
  //         marks1 => {
  //           this.marks1 = marks1
  //           for(let i=0; i<student1.length;i++){
  //             for(let j=0; j<marks1.length; j++){
  //               if(student1[i].studentId == marks1[j].studentId){
  //                 const subject = marks1[j].subjectName 
  //                 const marks = marks1[j].marks
  //                 studentId: student1[i].studentId
  //                 studentName: student1[i].firstName + student1[i].lastName





  //               }
  //             }

  //           }

  //         });


  //     });
  // } 

  getMarksByStudentIdOneByOne(student1: Student[]) {
    for(let i=0;i<this.student1.length;i++){
      this.marksService.getMarksById(student1[i].studentId).subscribe(marks2 => this.marks2 = marks2);
    }
  }


  fetchStudents() {

    this.http.get<any[]>('http://localhost:8080/api/student/getAll').subscribe(
      data => this.students = data,
      error => console.log(error)
    );

  }

  fetchSubjects() {

    this.http.get<any[]>('http://localhost:8080/api/subject/getSubjectNames').subscribe(
      data => this.subjects = data,
      error => console.log(error)
    );

  }



  markAttendance(studentId: number, present: boolean, subject: string, lectureNo: number, i: number) {
    if(subject=='' && lectureNo == 0){
      alert("Please enter subject and lecture no.")
    }
    else if (subject==''){
      alert("Please enter subject")
    }
    else if (lectureNo==0){
      alert("Please enter lecture no.")
    }
    else{
      const data = {
        lectureNo,
        subject,
        studentId,
        present 
      };       
      this.http.post('http://localhost:8080/api/marks/add', data).subscribe(
        () => console.log('Attendance marked successfully.'),
        error => console.log(error)
      );
      }
  }


  navBack(){
    this.router.navigate(['dashboard']);
  }

  showForm(){
    if(this.showUpdateMarksForm==false)
    this.showUpdateMarksForm=true;
    else
    this.showUpdateMarksForm=false;
  }

  showForm1(i: number){
    if(this.showUpdateForm[i]==false)
    this.showUpdateForm[i] = true;
    else
    this.showUpdateForm[i] = false;
  }

  subjectName: string[] = [];

  updateMarks(index: number){
    for (let i = 0; i < this.marks.length; i++){
      this.marks[i] = 0;
    }

    this.fetchSubjects();

    this.showForm();
    this.showMessage(index);
    // this.showForm();

    // this.subjectName = String(this.subjects).split(" ");
    console.log(this.subjects);
  }

  messages: string[] = [];
  showingMessages: boolean[] = [];
  marks: number[] = [];

  showMessage(index: number) {
    this.showingMessages[index] = true;
    this.messages[index] = 'Marks updated!';
    // this.buttonDisabled = true;
  }



  studentName :string= '';

  saveMarks(studentId: number, subjects: Subjects[], marks: number[], firstName: string, lastName: string , i: number ){
    console.log(subjects);    

    for (let i = 0; i < this.marks.length; i++) {
      const data = {
        studentId, subjectName: subjects[i], marks: marks[i], 
        studentName :firstName+ " " + lastName
      };

      this.http.post('http://localhost:8080/api/marks/add', data).subscribe(
        () => console.log('Marks updated successfully.'),
        error => console.log(error)
      );

    }
    alert("Marks updated successfully!");
    this.showForm();
    this.showMessage(i);

    // this.marksForm.reset();
    this.markForInput= 0;

  }

  bsModalRef!: BsModalRef;
  modalRef!: BsModalRef;
  fetchedData: Marks[]= [];
  fetchedData1: any;

  getSubjects(): void {
    this.subjectService.getSubjects().subscribe(subjects1 => this.subjects1 = subjects1);
  }

  getMarks(): void {
    this.marksService.getMarks().subscribe(marks1 => this.marks1 = marks1);
  }


  showViewAllMarks = false

  showAllMarks() {
    if (this.showViewAllMarks == false)
    {
      this.showViewAllMarks = true
    }
    else
    this.showViewAllMarks = false
  }

  viewAllMarks(){
    this.marksService.getMarks().subscribe((data) => {
      this.fetchedData = data;
      this.modalRef = this.modalService.show(ViewAllMarksComponent, {
        initialState: {
          data: this.fetchedData,
        },
      });
    });

    this.showAllMarks();




  }

  viewMarks(studentId: number){

    this.studentService.getStudentById(studentId).subscribe((data1) => {
      this.fetchedData1 = data1;
      this.modalRef = this.modalService.show(ViewStudentMarksComponent, {
        initialState: {
          data1: this.fetchedData1,
        },
      });
    });




  }

  data1: any;
  data2: any;


  openModalWithData(data1: any, data2: any) {
    const initialState = {
      data1: data1,
      data2: data2
    };
    this.modalService.show(ViewStudentMarksComponent, { initialState });
  }

  fetchData(studentId: number) {
    this.marksService.fetchSubjects().subscribe(data1 => {
      this.studentService.getStudentById(studentId).subscribe(data2 => {
        this.openModalWithData(data1, data2);
      });
    });

  }




}
<div class="dashboard-container">
    <div class="sidebar">
      <div class="logo-section">
        <img src="./assets/logo.png" alt="Logo" width="100%" height="170px">
      </div>
      <ul class="navigation-menu">

        <li><a href="dashboard">Dashboard</a></li>
        <li><a href="department">Department</a></li>
        <li><a href="staff">Staff</a></li>
        <li><a href="subjects">Subjects</a></li>
        <li><a href="semester">Semester</a></li>
        <li><a href="student">Student</a></li>
        <li><a href="attendance">Attendance</a></li>
        <li><a href="marks">Marks</a></li>
        <li><a href="reports">Reports</a></li>
      </ul>
    </div>
    <div class="content">
        <div class="top-bar">
          <button (click)="navBack()" class="update-button">Back</button>
        </div>

        <div class="headAndButton">
          <h2>Marks Details</h2>
          <button class="update-button" (click)="viewAllMarks()">View Complete Marks Details</button>
        </div>

        <div *ngIf =viewAllMarks>
          <table>
          <thead>
            <tr>
              <th>Student ID</th>
              <th>Name</th>
              <th *ngFor="let s of subjects1">{{ s.courseName }}</th>
              <!-- <th>Marks</th> -->
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let d of marks1 , let i = index">
              <td>{{ d.studentId }}</td>
              <td>{{ d.studentName}}</td>
              <td>{{ d.subjectName}}</td>
              <td>{{ d.marks}}</td>

            </tr>


          </tbody>
        </table>

        </div>


        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>USN No.</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let student of students, let i = index">
              <td>{{ student.studentId }}</td>
              <td>{{ student.usnno }}</td>
              <td>{{ student.firstName }}</td>
              <td>{{ student.lastName }}</td>
              <td>
                <button class="update-button" (click)="viewMarks(student.studentId)">View Marks</button>
                <button class="update-button" (click)="updateMarks(i)">Update Marks</button>


                <div *ngIf="showUpdateMarksForm">
                  <table class="insideTable" [hidden]="!showingMessages[i]">
                    <tr *ngFor="let subject of subjects, let i = index">
                      <td>{{ subject }} : </td>
                      <td><input type="number" #markForInput id="marks[i]" [(ngModel)]="marks[i]" value=" "></td>
                    </tr>
                    <tr>
                      <td><button class="save-button" (click)="saveMarks(student.studentId, subjects, marks, student.firstName, student.lastName, i ) "> Save </button></td>
                    </tr>
                  </table>

                </div>



              </td>
            </tr>
          </tbody>
        </table>



    </div>
  </div>



