import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { Student } from 'src/app/models/student';
import { StudentService } from 'src/app/services/student.service';

@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.css']
})
export class StudentComponent implements OnInit {
  // students: any;
  studentForm: FormGroup;
  students: Student[] = [];
  showAddStudent = false;

  USNNo = '';
  firstName = '';
  middleName = '';
  lastName = '';
  fatherName = '';
  motherName = '';
  parentEmail = '';
  fatherPhone = '';
  motherPhone = '';
  gender = '';
  dob = '';
  email = '';
  phone = '';
  yearOfAdmission = '';
  line1 = '';
  line2 = '';
  landmark = '';
  city = '';
  street = '';
  district = '';
  pincode = '';
  country = '';
  state = '';


  constructor(private studentService: StudentService, private router: Router, private formBuilder: FormBuilder) { }


  initForm() {
    this.studentForm = this.formBuilder.group({
      firstName: [null, Validators.required],
      middleName: [null],
      lastName: [null, Validators.required],
      USNNo: [null, Validators.required],
      email: [null, [Validators.required, Validators.email]],
      phone: [null, Validators.required],
      dob: [null, Validators.required],
      gender: [null, Validators.required],

      yearOfAdmission: [null, [Validators.required]],
      address: {
        line1: [null, [Validators.required]],
        line2: [null, [Validators.required]],
        landmark: [null, [Validators.required]],
        city: [null, [Validators.required]],
        Street: [null, [Validators.required]],
        pincode: [null, [Validators.required]],
        district: [null, [Validators.required]],
        state: [null, [Validators.required]],
        country: [null, [Validators.required]],
      },

      fatherName: [null, [Validators.required]],
      fatherPhone: [null, [Validators.required]],
      motherName: [null, [Validators.required]],
      motherPhone: [null, [Validators.required]],
      parentEmail: [null, [Validators.required, Validators.email]]
    });
  }

    navBack(){
    this.router.navigate(['dashboard']);
  }

  ngOnInit() {
    this.initForm();
    this.loadStudents();
  }

  loadStudents() {
    this.studentService.getStudents().subscribe(
      students => {
        this.students = students;
      },
      error => {
        console.log('Error occurred while fetching students:', error);
      }
    );
  }

  getStudents(): void {
    this.studentService.getStudents()
      .subscribe(students => this.students = students);
  }

  showAddStudentForm(){
    this.showAddStudent = true;
  }


  addStudent(studentData: Student) {
    if (this.studentForm.valid) {
      this.studentService.createStudent(studentData).subscribe(
        createdStudent => {
          console.log('Student created successfully:', createdStudent);
          this.studentForm.reset();
          this.loadStudents();
        },
        error => {
          console.log('Error occurred while creating student:', error);
        }
      );
    }
  }

  updateStudent(student: Student) {
    this.studentService.updateStudent(student.id, student).subscribe(
      updatedStudent => {
        console.log('Student updated successfully:', updatedStudent);
        this.loadStudents();
      },
      error => {
        console.log('Error occurred while updating student:', error);
      }
    );
  }

  deleteStudent(id: number) {
    this.studentService.deleteStudent(id).subscribe(
      () => {
        console.log('Student deleted successfully');
        this.loadStudents();
      },
      error => {
        console.log('Error occurred while deleting student:', error);
      }
    );
  }
}







