import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { BsModalService, BsModalRef } from 'ngx-bootstrap/modal';
import { Student } from 'src/app/models/student';
import { StudentService } from 'src/app/services/student.service';
import { StudentComponent } from '../../student/student.component';

@Component({
  selector: 'app-view-student',
  templateUrl: './view-student.component.html',
  styleUrls: ['./view-student.component.css']
})
export class ViewStudentComponent implements OnInit {

  students: Student[] = [];
  student!: Student;
  showAddStudent = false;
  sortByFirstName = false;
  sortByUSNNo = false;

  studentId: number =0;

  // usnno = '';
  // firstName = '';
  // middleName = '';
  // lastName = '';
  // fatherName = '';
  // motherName = '';
  // parentEmail = '';
  // fatherPhone = '';
  // motherPhone = '';
  // gender = '';
  // dob = '';
  // email = '';
  // phone = '';
  // yearOfAdmission = '';
  // address = '';

  id = 0;


  constructor(private s: StudentComponent, private studentService: StudentService, private router: Router, private formBuilder: FormBuilder, private modalService: BsModalService, public bsModalRef: BsModalRef) { }

  navBack(){
    this.router.navigate(['dashboard']);
  }
  // studentForm!: FormGroup;

  ngOnInit() {
    this.id = this.s.getId();
    this.getStudentById(this.id);
  }


  loadStudents() {
    this.studentService.getStudents().subscribe(
      students => {
        this.students = students;
      },
      error => {
        console.log('Error occurred while fetching students:', error);
      }
    );
  }

  getStudentById(studentId: number) {
    this.studentService.getStudentById(studentId)
    .subscribe(students => this.student = students);
  }

  showModal = false;
  title: string = 'Modal Title';

  close() {
    this.bsModalRef.hide();
    // this.showModal = false
  }

}
