import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Student } from 'src/app/models/student';
import { Router } from '@angular/router';

@Component({
  selector: 'app-attendance',
  templateUrl: './attendance.component.html',
  styleUrls: ['./attendance.component.css']
})
export class AttendanceComponent {
  students: Student[] = [];
  subject: string = '';
  lectureno: number = 0;
  showAttendance = false;

  constructor(private http: HttpClient, private router: Router) { }

  ngOnInit() {
    this.fetchStudents();
    // this.showAttendanceStatus();
    // this.showAttendance = false;

  }

  showAttendanceStatus(){
    if(this.showAttendance==false)
    this.showAttendance=true;
    else
    this.showAttendance=false;
  }

  fetchStudents() {
    this.http.get<any[]>('http://localhost:8080/api/attendance/students').subscribe(
      data => this.students = data,
      error => console.log(error)
    );

  }

  markAttendance(studentId: number, present: boolean) {
    const data = {

      // lectureno: this.lectureno,
      subject: this.subject,
      studentId,
      present 
    };


    this.http.post('http://localhost:8080/api/attendance/add', data).subscribe(
      () => console.log('Attendance marked successfully.'),
      error => console.log(error)
    );

    // this.showAttendance = true;
  }

  navBack(){
    this.router.navigate(['dashboard']);
  }
}








