import { Component } from '@angular/core';
import { FormBuilder, FormGroup, FormControl } from '@angular/forms';
import { Router } from '@angular/router';
import { StaffService } from 'src/app/services/staff.service';

@Component({
  selector: 'app-staff',
  templateUrl: './staff.component.html',
  styleUrls: ['./staff.component.css']
})
export class StaffComponent {
students: any;
showAddStaffForm = false;

showDepartment(){
  if(this.showAddStaffForm == false){
    this.showAddStaffForm = true;
  }
  else
  this.showAddStaffForm = false;

}

constructor(private departmentService: StaffService, private router: Router, private formBuilder: FormBuilder) { }

deptForm!: FormGroup;

  navBack(){
    this.router.navigate(['dashboard']);
  }
}
